import React from 'react';

import './CardDetail.css';

function CardDetail({current}) {
    return (
      <div className="card-detail">
      <h1 className="card-detail__title--blue">{current.day.condition.text}</h1>
      <p className="card__date">{current.date}</p> 
      <img src={current.day.condition.icon} alt="Avatar" className="card__image"/>
      <div className="container">
        <h4><b>{`${current.day.maxtemp_c} / ${current.day.mintemp_c}`}</b></h4>
          <br/>
          <br/>
        <p className="card__text">{current.day.condition.text}</p> 
        </div>
        </div>
    );
  }

  export default CardDetail;
