import React, { Component } from 'react';
import Navbar from '../Components/Navbar/Navbar';
import LeftPanel from '../Components/CardDetail/CardDetail'
import CardGrid from '../Components/CardGrid/CardGrid'
//import { robots } from './robots';
import './App.css';



class App extends Component {
    constructor() {
        super()
        this.state = {
            data: {}
        }
    }

    componentDidMount() {
      const url = "https://api.apixu.com/v1/forecast.json?key=548af1ca9ec24d1cb33175649191408&q=Zapopan&days=7";

        fetch(url)
          .then(response => response.json())
          .then(forecasts => {
            this.setState({ data: forecasts });
            console.log(forecasts);
            }
          );
    }

    onSearchChange = (event) => {
        this.setState({ searchfield: event.target.value })
    }


    render() {

        return (
          <div className="app">

          <div className="app__container">
            <CardGrid forecasts={this.state.data.forecast} className="app__container-item" />
          </div>
        </div>
        );
    }
}

export default App;
