import React from 'react';
import Card from '../Card/Card';
import './CardGrid.css';

const CardGrid = ({ forecast, newForecast, toggleTempSelect,  tempValue}) => {

  console.log("CardGrid FCD ", forecast);
  if (forecast) {
    console.log("CardGrid FCD ", forecast.forecastday);
  }

  return (

    <div className="cards-container">
      <button onClick={newForecast}className="btn btn--info">New forecast</button>
      <div className="cards-container__items">


      <select id="lang" onChange={toggleTempSelect} value={tempValue}>
              <option value="select">Select</option>
              <option value="Java">Java</option>
              <option value="C++">C++</option>
           </select>
           <p></p>
           <p>{this.state.value}</p>



      <select onChange={toggleTempSelect} className="select">
        <option value="C">C</option>
         <option value="F">F</option>
      </select>
     </div>


    <div className="card-grid">

      {
        forecast ?
          forecast.forecastday.map((forecastday, index) => {
            return (
              <Card
                key={index}
                date={forecastday.date}
                day={forecastday.day}
                text={forecastday.text}
              />
            );
          })
          :
          <h1>LOADING...</h1>
      }
    </div>
    </div>
  );

  // return (

  //     <div className="card-grid">
  //        <Card/>
  //        <Card/>
  //     </div>
  //   );
}

export default CardGrid;
